import{_ as a}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as d,f as c}from"./app.7bc693ea.js";const o={},s=c('<h2 id="pandas-数据读取" tabindex="-1"><a class="header-anchor" href="#pandas-数据读取" aria-hidden="true">#</a> Pandas 数据读取</h2><p>使用pd.read_csv读取数据</p><p><code>fapath = &quot;***&quot;</code></p><p><code>data = pd.read_csv(fapath,sep=&#39;&#39;,header=None,names=[] ) </code></p><details class="custom-container details"><summary>数据探索相关</summary><p>查看前几行数据: <code>data.head</code></p><p>常看数据的形状：<code>data.shape</code></p><p>查看列名列表：<code>data.columns</code></p><p>常看索引列：<code>data.index</code></p><p>查看每列的数据类型：<code>data.dtypes</code></p></details><h2 id="pandas-的数据结构" tabindex="-1"><a class="header-anchor" href="#pandas-的数据结构" aria-hidden="true">#</a> Pandas 的数据结构</h2><h3 id="dataframe-二维数据-整个表格-多行多列" tabindex="-1"><a class="header-anchor" href="#dataframe-二维数据-整个表格-多行多列" aria-hidden="true">#</a> DataFrame：二维数据，整个表格，多行多列</h3><p>查询一列：<code>data[&#39;&#39;]</code></p><p>查询多列：<code>data[[&#39;&#39;,&#39;&#39;]]</code></p><p>查询一行：<code>data.loc[1]</code> : 返回索引为 1 的这一行</p><p>查询多行：<code>data.loc[1:3]</code>：返回索引 1 到 3 行，与 python 列表的切片不同。<mark>返回结果包含第 3 行</mark></p><h3 id="series-一维数据-一行或一列" tabindex="-1"><a class="header-anchor" href="#series-一维数据-一行或一列" aria-hidden="true">#</a> Series：一维数据，一行或一列</h3><p>创建 Series</p><p><code>S_1 = pd.Series([],index = [&#39;&#39;,&#39;&#39;])</code></p><p><code>S_2 = pd.Series({key:value,})</code></p><p>获取索引：<code>S_1.index</code></p><p>获取数据：<code>S_1.values</code></p><h2 id="pandas-数据查询" tabindex="-1"><a class="header-anchor" href="#pandas-数据查询" aria-hidden="true">#</a> Pandas 数据查询</h2>',18),r=[s];function p(t,n){return e(),d("div",null,r)}const _=a(o,[["render",p],["__file","03. Pandas 小记.html.vue"]]);export{_ as default};
